const dropDownMenu=document.querySelector(".drop-down-menu"),btnBurgerMenu=document.querySelector(".header__burger-menu"),backdrop=document.querySelector(".backdrop");btnBurgerMenu.addEventListener("click",()=>{dropDownMenu.classList.toggle("drop-down-menu--active"),backdrop.classList.toggle("hidden")}),dropDownMenu.addEventListener("click",e=>{e.target===dropDownMenu&&backdrop.classList.remove("hidden")});